import React from 'react'
import './style.css';
import { CommentListItem } from 'types/interface';
import defaultProfileImage from 'assets/image/default-profile-image.png';

interface Props {
    commentListItem : CommentListItem; 

}

export default function CommentItem({commentListItem } : Props) {

    const {nickname, profileImage, writeDatetime, content} = commentListItem;

  return (
    <div className='comment-list-item'>
        <div className='comment-list-item-top'>
            <div className='comment-list-item-profile-box'></div>
                <div className='comment-list-profile-image' style={{ backgroundImage : `url(${profileImage ? profileImage : defaultProfileImage })`}}></div>
            <div className='comment-list-item-nickname'>{nickname}</div>
            <div className='comment-list-item-divider'>{'\|'}</div>
            <div className='comment-list-item-time'>{writeDatetime}</div>
        </div>
        <div className='comment-list-item-main'></div>
            <div className='comment-list-item-content'>{content}</div>
     </div>
  )
}
